mapper.select:
SELECT A.ID AS POST_ID , B.ID AS MEMBER_ID , B.NICKNAME AS MEMBER_NICKNAME , A.TITLE AS POST_TITLE , A.START_DATE AS START_DATE , A.END_DATE AS END_DATE , A.EXPIRES_AT AS EXPIRES_AT , A.STATUS AS STATUS , A.CAPACITY AS CAPACITY , COUNT(D.ID) AS PARTICIPANT_COUNT , A.COMMENT_COUNT AS COMMENT_COUNT , A.CREATED_AT AS CREATED_AT , A.UPDATED_AT AS UPDATED_AT , A.VISIBILITY AS VISIBILITY , C.NAME AS RANK_NAME FROM STUDY_RECRUIT_POST A JOIN MEMBER B ON A.MEMBER_ID = B.ID JOIN MEMBER_RANK C ON B.RANK_ID = C.ID LEFT JOIN STUDY_RECRUIT_MEMBER D ON A.ID = D.POST_ID AND D.STATUS = 'APPROVED'
A.VISIBILITY = 'Y'
AND A.STATUS = #{status}
AND (A.TITLE LIKE CONCAT('%', #{keyword}, '%') OR A.CONTENT LIKE CONCAT('%', #{keyword}, '%'))
GROUP BY A.ID ORDER BY A.ID DESC
LIMIT #{size}
OFFSET #{offset}

SELECT A.ID AS POST_ID , B.ID AS MEMBER_ID , B.NICKNAME AS MEMBER_NICKNAME , A.TITLE AS POST_TITLE , A.START_DATE AS START_DATE , A.END_DATE AS END_DATE , A.EXPIRES_AT AS EXPIRES_AT , A.STATUS AS STATUS , A.CAPACITY AS CAPACITY , COUNT(D.ID) AS PARTICIPANT_COUNT , A.COMMENT_COUNT AS COMMENT_COUNT , A.CREATED_AT AS CREATED_AT , A.UPDATED_AT AS UPDATED_AT , A.VISIBILITY AS VISIBILITY , C.NAME AS RANK_NAME FROM STUDY_RECRUIT_POST A JOIN MEMBER B ON A.MEMBER_ID = B.ID JOIN MEMBER_RANK C ON B.RANK_ID = C.ID LEFT JOIN STUDY_RECRUIT_MEMBER D ON A.ID = D.POST_ID AND D.STATUS = 'APPROVED'
A.VISIBILITY = 'N'
AND A.STATUS = #{status}
AND (A.TITLE LIKE CONCAT('%', #{keyword}, '%') OR A.CONTENT LIKE CONCAT('%', #{keyword}, '%'))
GROUP BY A.ID ORDER BY A.ID DESC
LIMIT #{size}
OFFSET #{offset}
